import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, ScrollView, TouchableOpacity, Linking, ImageBackground } from 'react-native';
import { IObjects } from '../constants/types';

const Objects: React.FC = () => {
  const [objects, setObjects] = useState<IObjects[]>([]);

  useEffect(() => {
    fetch('http://66.71.1.174:3000/objects')
      .then(response => response.json())
      .then(data => setObjects(data))
      .catch(error => console.log(error));
  }, []);

  const renderObjects = () => {
    return objects.map((object: IObjects) => {
      return (
        <TouchableOpacity key={object.object_id} onPress={() => Linking.openURL(object.link ?? '')}>
          <ImageBackground source={{ uri: object.image_url }} style={styles.box}>
            <View style={styles.textContainer}>
              <Text style={styles.title}>{object.title}</Text>
              <View style={styles.buttonContainer}>
                <Text style={styles.buttonText}>{object.linkLabel}</Text>
              </View>
            </View>
          </ImageBackground>
        </TouchableOpacity>
      );
    });
  };

  return <ScrollView contentContainerStyle={styles.container}>{renderObjects()}</ScrollView>;
};

const styles = StyleSheet.create({
  container: {
    padding: 10,
  },
  box: {
    height: 200,
    borderRadius: 10,
    marginBottom: 20,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.2,
    shadowRadius: 2,
    elevation: 3,
  },
  textContainer: {
    flex: 1,
    justifyContent: 'space-between',
    alignItems: 'flex-start',
    backgroundColor: 'rgba(0,0,0,0.4)',
    padding: 10,
    borderRadius: 10,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    color: '#fff',
  },
  buttonContainer: {
    alignSelf: 'flex-end',
    backgroundColor: '#fff',
    borderRadius: 5,
    paddingVertical: 5,
    paddingHorizontal: 10,
  },
  buttonText: {
    fontSize: 16,
    color: '#007AFF',
  },
});

export default Objects;
